INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (800, 399280, 4, N'73.20 ', N'Market research and public opinion polling ', N'7320 ', N'This class includes:- investigation into market potential, awareness, acceptance and familiarity of goods and services and buying habits of consumers for the purpose of sales promotion and development of new goods and services, including statistical analyses of the results- investigation into collective opinions of the public about political, economic and social issues and statistical analysis thereof ', N'', N'- Undercover market research, to assess service level by visiting or calling business- Testing of website usability ', N'', N'73.2 ', 799)
