INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (805, 399285, 4, N'74.20 ', N'Photographic activities ', N'7420 ', N'This class includes:- commercial and consumer photograph production:  • portrait photography for passports, schools, weddings etc.  • photography for commercials, publishers, fashion, real estate or tourism purposes  • aerial photography   • videotaping of events: weddings, meetings etc.- film processing:  • developing, printing and enlarging from client-taken negatives or cine-films  • film developing and photo printing laboratories   • one hour photo shops (not part of camera stores)  • mounting of slides  • copying and restoring or transparency retouching in connection with photographs- activities of photojournalists ', N'This class also includes:- microfilming of documents ', N'- Transferring content from video tapes (and similar media) to other media- Object scanning in 3D ', N'This class excludes:- processing motion picture film related to the motion picture and television industries, see 59.12- cartographic and spatial information activities, see 71.12- operation of coin-operated (self-service) photo machines, see 96.09 ', N'74.2 ', 804)
