INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (812, 399292, 4, N'75.00 ', N'Veterinary activities ', N'7500 ', N'This class includes:- animal health care and control activities for farm animals- animal health care and control activities for pet animalsThese activities are carried out by qualified veterinarians when working in veterinary hospitals as well as when visiting farms, kennels or homes, in own consulting and surgery rooms or elsewhere. ', N'This class also includes:- activities of veterinary assistants or other auxiliary veterinary personnel- clinico-pathological and other diagnostic activities pertaining to animals- animal ambulance activities ', N'- Animal genetic testing activities ', N'This class excludes:- farm animal boarding activities without health care, see 01.62- sheep shearing, see 01.62- herd testing services, droving services, agistment services, poultry caponising, see 01.62- activities related to artificial insemination, see 01.62- pet animal boarding activities without health care, see 96.09 ', N'75.0 ', 811)
