INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (813, 399293, 1, N'N ', N'ADMINISTRATIVE AND SUPPORT SERVICE ACTIVITIES ', N'N ', N'This section includes a variety of activities that support general business operations. These activities differ from those in section M, since their primary purpose is not the transfer of specialised knowledge. ', N'', N'', N'', N'', NULL)
