GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (816, 399296, 4, N'77.11 ', N'Rental and leasing of cars and light motor vehicles ', N'7710 ', N'This class includes:- rental and operational leasing of the following types of vehicles:  • passenger cars and other light motor vehicles (with a weight not exceeding 3,5 tons) without driver ', N'', N'', N'This class excludes:- rental or leasing of cars or light motor vehicles with driver, see 49.32, 49.39 ', N'77.1 ', 815)
