INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (817, 399297, 4, N'77.12 ', N'Rental and leasing of trucks ', N'7710 ', N'This class includes:- rental and operational leasing of the following types of vehicles:  • trucks, utility trailers and heavy motor vehicles (with a weight exceeding 3,5 tons)  • recreational vehicles ', N'', N'', N'This class excludes:- rental or leasing of heavy goods vehicles or trucks with driver, see 49.41 ', N'77.1 ', 815)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (818, 399298, 3, N'77.2 ', N'Rental and leasing of personal and household goods ', N'772 ', N'This group includes the rental of personal and household goods as well as rental of recreational and sports equipment and video tapes. Activities generally include short-term rental of goods although in some instances, the goods may be leased for longer periods of time. ', N'', N'', N'', N'77 ', 814)
