INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (819, 399299, 4, N'77.21 ', N'Rental and leasing of recreational and sports goods ', N'7721 ', N'This class includes rental of recreational and sports equipment:- pleasure boats, canoes, sailboats- bicycles- beach chairs and umbrellas- other sports equipment- skis ', N'', N'- Rental of yachts without operator ', N'This class excludes:- rental of pleasure boats and sailing boats with crew, see 50.10, 50.30- rental of video tapes and disks, see 77.22- rental of other personal and household goods n.e.c., see 77.29- rental of leisure and pleasure equipment as an integral part of recreational facilities, see 93.29 ', N'77.2 ', 818)
