INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (820, 399300, 4, N'77.22 ', N'Rental of video tapes and disks ', N'7722 ', N'This class includes:- rental of video tapes, records, CDs, DVDs etc. ', N'', N'', N'', N'77.2 ', 818)
