INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (823, 399303, 4, N'77.31 ', N'Rental and leasing of agricultural machinery and equipment ', N'7730 ', N'This class includes:- rental and operational leasing of agricultural and forestry machinery and equipment without operator:  • rental of products produced by class 28.30, such as agricultural tractors etc. ', N'', N'', N'This class excludes:- rental of agricultural and forestry machinery or equipment with operator, see 01.61, 02.40 ', N'77.3 ', 822)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (824, 399304, 4, N'77.32 ', N'Rental and leasing of construction and civil engineering machinery and equipment ', N'7730 ', N'This class includes:- rental and operational leasing of construction and civil engineering machinery and equipment without operator:  • crane lorries  • scaffolds and work platforms, without erection and dismantling ', N'', N'', N'This class excludes:- rental of construction and civil engineering machinery or equipment with operator, see division 43 ', N'77.3 ', 822)
