INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (825, 399305, 4, N'77.33 ', N'Rental and leasing of office machinery and equipment (including computers) ', N'7730 ', N'This class includes:- rental and operational leasing of office machinery and equipment without operator:  • computers and computer peripheral equipment  • duplicating machines, typewriters and word-processing machines  • accounting machinery and equipment: cash registers, electronic calculators etc.  • office furniture ', N'', N'', N'', N'77.3 ', 822)
