INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (826, 399306, 4, N'77.34 ', N'Rental and leasing of water transport equipment ', N'7730 ', N'This class includes:- rental and operational leasing of water-transport equipment without operator:   • commercial boats and ships ', N'', N'', N'This class excludes:- rental of water-transport equipment with operator, see division 50- rental of pleasure boats, see 77.21 ', N'77.3 ', 822)
