INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (827, 399307, 4, N'77.35 ', N'Rental and leasing of air transport equipment ', N'7730 ', N'This class includes:- rental and operational leasing of air transport equipment without operator:  • airplanes  • hot-air balloons ', N'', N'', N'This class excludes:- rental of air-transport equipment with operator, see division 51 ', N'77.3 ', 822)
