INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (828, 399308, 4, N'77.39 ', N'Rental and leasing of other machinery, equipment and tangible goods n.e.c. ', N'7730 ', N'This class includes:- rental and operational leasing, without operator, of other machinery and equipment that are generally used as capital goods by industries:  • engines and turbines  • machine tools  • mining and oilfield equipment  • professional radio, television and communication equipment  • motion picture production equipment  • measuring and controlling equipment  • other scientific, commercial and industrial machinery- rental and operational leasing of land-transport equipment (other than motor vehicles) without drivers:  • motorcycles, caravans and campers etc.  • railroad vehicles ', N'This class also includes:- rental of accommodation or office containers- rental of animals (e.g. herds, race horses)- rental of containers- rental of pallets ', N'', N'This class excludes:- rental of bicycles, see 77.21- rental of agricultural and forestry machinery and equipment, see 77.31- rental of construction and civil engineering machinery and equipment, see 77.32- rental of office machinery and equipment, including computers, see 77.33 ', N'77.3 ', 822)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (829, 399309, 3, N'77.4 ', N'Leasing of intellectual property and similar products, except copyrighted works ', N'774 ', N'', N'', N'', N'', N'77 ', 814)
