INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (833, 399313, 4, N'78.10 ', N'Activities of employment placement agencies ', N'7810 ', N'This class includes listing employment vacancies and referring or placing applicants for employment, where the individuals referred or placed are not employees of the employment agencies.This class includes:- personnel search, selection referral and placement activities, including executive placement and search activities- activities of casting agencies and bureaus, such as theatrical casting agencies- activities of on-line employment placement agencies ', N'', N'- Sports talent scout ', N'This class excludes:- activities of personal theatrical or artistic agents or agencies, see 74.90 ', N'78.1 ', 832)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (834, 399314, 3, N'78.2 ', N'Temporary employment agency activities ', N'782 ', N'', N'', N'', N'', N'78 ', 831)
