INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (835, 399315, 4, N'78.20 ', N'Temporary employment agency activities ', N'7820 ', N'This class includes the activities of supplying workers to clients'' businesses for limited periods of time to temporarily replace or supplement the working force of the client, where the individuals provided are employees of the temporary help service unit. However, units classified here do not provide direct supervision of their employees at the clients'' work sites. ', N'', N'', N'', N'78.2 ', 834)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (836, 399316, 3, N'78.3 ', N'Other human resources provision ', N'783 ', N'', N'', N'', N'', N'78 ', 831)
