INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (837, 399317, 4, N'78.30 ', N'Other human resources provision ', N'7830 ', N'This class includes the activities of providing human resources for client businesses. The units classified here represent the employer of record for the employees on matters relating to payroll, taxes, and other fiscal and human resource issues, but they are not responsible for direction and supervision of employees.The provision of human resources is typically done on a long-term or permanent basis and the units classified here perform a wide range of human resource and personnel management duties associated with this provision. ', N'', N'', N'This class excludes:- provision of human resources functions together with supervision or running of the business, see the class in the respective economic activity of that business- provision of human resources to temporarily replace or supplement the workforce of the client, see 78.20 ', N'78.3 ', 836)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (838, 399318, 2, N'79 ', N'Travel agency, tour operator and other reservation service and related activities ', N'79 ', N'This division includes the activity of agencies, primarily engaged in selling travel, tour, transportation and accommodation services to the general public and commercial clients and the activity of arranging and assembling tours that are sold through travel agencies or directly by agents such as tour operators; and other travel-related services including reservation services. ', N'The activities of tourist guides and tourism promotion activities are also included. ', N'', N'', N'N ', 813)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (839, 399319, 3, N'79.1 ', N'Travel agency and tour operator activities ', N'791 ', N'This group includes the activities of agencies, primarily engaged in selling travel, tour, transportation and accommodation services to the general public and commercial clients and the activity of arranging and assembling tours that are sold through travel agencies or directly by agents such as tour operators. ', N'', N'', N'', N'79 ', 838)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (840, 399320, 4, N'79.11 ', N'Travel agency activities ', N'7911 ', N'This class includes:- activities of agencies, primarily engaged in selling travel, tour, transportation and accommodation services on a wholesale or retail basis to the general public and commercial clients ', N'', N'', N'', N'79.1 ', 839)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (841, 399321, 4, N'79.12 ', N'Tour operator activities ', N'7912 ', N'This class includes:- arranging and assembling tours that are sold through travel agencies or directly by tour operators. The tours may include any or all of the following:  • transportation  • accommodation  • food  • visits to museums, historical or cultural sites, theatrical, musical or sporting events ', N'', N'', N'', N'79.1 ', 839)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (842, 399322, 3, N'79.9 ', N'Other reservation service and related activities ', N'799 ', N'', N'', N'', N'', N'79 ', 838)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (843, 399323, 4, N'79.90 ', N'Other reservation service and related activities ', N'7990 ', N'This class includes:- other travel-related reservation services:  • reservations for transportation, hotels, restaurants, car rentals, entertainment and sport etc.- time-share exchange services- ticket sales activities for theatrical, sports and other amusement and entertainment events- visitor assistance services:  • provision of travel information to visitors  • activities of tourist guides- tourism promotion activities ', N'', N'', N'This class excludes:- the activities of travel agencies and tour operators, see 79.11, 79.12- organisation and management of events such as meetings, conventions and conferences, see 82.30 ', N'79.9 ', 842)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (844, 399324, 2, N'80 ', N'Security and investigation activities ', N'80 ', N'This division includes security-related services such as: investigation and detective services; guard and patrol services; picking up and delivering money, receipts, or other valuable items with personnel and equipment to protect such properties while in transit; operation of electronic security alarm systems, such as burglar and fire alarms, where the activity focuses on remote monitoring these systems, but often involves also sale, installation and repair services. If the latter components are provided separate, they are excluded from this division and classified in retail sale, construction etc. ', N'', N'', N'', N'N ', 813)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (845, 399325, 3, N'80.1 ', N'Private security activities ', N'801 ', N'', N'', N'', N'', N'80 ', 844)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (846, 399326, 4, N'80.10 ', N'Private security activities ', N'8010 ', N'This class includes the provision of one or more of the following: guard and patrol services, picking up and delivering money, receipts, or other valuable items with personnel and equipment to protect such properties while in transit.This class includes:- armoured car services- bodyguard services- polygraph services- fingerprinting services- security guard services- security shredding of information on any media ', N'', N'- Support services to cash collection and deposit services ', N'This class excludes:- public order and safety activities, see 84.24 ', N'80.1 ', 845)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (847, 399327, 3, N'80.2 ', N'Security systems service activities ', N'802 ', N'', N'', N'', N'', N'80 ', 844)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (848, 399328, 4, N'80.20 ', N'Security systems service activities ', N'8020 ', N'This class includes:- monitoring or remote monitoring of electronic security alarm systems, such as burglar and fire alarms, including their installation and maintenance- installing, repairing, rebuilding, and adjusting mechanical or electronic locking devices, safes and security vaults in connection with later monitoring and remote monitoringThe units carrying out these activities may also engage in selling such security systems, mechanical or electronic locking devices, safes and security vaults. ', N'', N'', N'This class excludes:- installation of security systems, such as burglar and fire alarms, without later monitoring, see 43.21- retail sale of electrical security alarm systems, mechanical or electronic locking devices, safes and security vaults in specialised stores, without monitoring, installation or maintenance services, see 47.59- security consultants, see 74.90- public order and safety activities, see 84.24- providing key duplication services, see 95.29 ', N'80.2 ', 847)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (849, 399329, 3, N'80.3 ', N'Investigation activities ', N'803 ', N'', N'', N'', N'', N'80 ', 844)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (850, 399330, 4, N'80.30 ', N'Investigation activities ', N'8030 ', N'This class includes:- investigation and detective service activities- activities of all private investigators, independent of the type of client or purpose of investigation ', N'', N'', N'', N'80.3 ', 849)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (851, 399331, 2, N'81 ', N'Services to buildings and landscape activities ', N'81 ', N'This division includes the provision of a number of general support services, such as the provision of a combination of support services within a client''s facilities, the interior and exterior cleaning of buildings of all types, cleaning of industrial machinery, cleaning of trains, buses, planes, etc., cleaning of the inside of road and sea tankers, disinfecting and exterminating activities for buildings, ships, trains, etc., bottle cleaning, street sweeping, snow and ice removal, provision of landscape care and maintenance services and provision of these services along with the design of landscape plans and/or the construction (i.e. installation) of walkways, retaining walls, decks, fences, ponds, and similar structures. ', N'', N'', N'', N'N ', 813)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (852, 399332, 3, N'81.1 ', N'Combined facilities support activities ', N'811 ', N'', N'', N'', N'', N'81 ', 851)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (853, 399333, 4, N'81.10 ', N'Combined facilities support activities ', N'8110 ', N'This class includes the provision of a combination of support services within a client''s facilities. These services include general interior cleaning, maintenance, trash disposal, guard and security, mail routing, reception, laundry and related services to support operations within facilities. These support activities are performed by operating staff, which is not involved with or responsible for the core business or activities of the client. ', N'', N'', N'This class excludes:- provision of only one of the support services (e.g. general interior cleaning services) or addressing only a single function (e.g. heating), see the appropriate class according to the service provided- provision of management and operating staff for the complete operation of a client''s establishment, such as a hotel, restaurant, mine, or hospital, see the class of the unit operated- provision of on site management and operation of a client''s computer systems and/or data processing facilities, see 62.03- operation of correctional facilities on a contract or fee basis, see 84.23 ', N'81.1 ', 852)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (854, 399334, 3, N'81.2 ', N'Cleaning activities ', N'812 ', N'This group includes the activities of general interior cleaning of all types of buildings, exterior cleaning of buildings, specialised cleaning activities for buildings or other specialised cleaning activities, cleaning of industrial machinery, cleaning of the inside of road and sea tankers, disinfecting and extermination activities for buildings and industrial machinery, bottle cleaning, street sweeping, snow and ice removal. ', N'', N'', N'This group excludes:- agricultural pest control, see 01.61- cleaning of new buildings immediately after construction, 43.39- steam-cleaning, sand blasting and similar activities for building exteriors, see 43.99- carpet and rug shampooing, drapery and curtain cleaning, see 96.01 ', N'81 ', 851)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (855, 399335, 4, N'81.21 ', N'General cleaning of buildings ', N'8121 ', N'This class includes:- general (non-specialized) cleaning of all types of buildings, such as:  • offices   • houses or apartments  • factories   • shops   • institutions- general (non-specialized) cleaning of other business and professional premises and multiunit residential buildingsThese activities are mostly interior cleaning although they may include the cleaning of associated exterior areas such as windows or passageways. ', N'', N'', N'This class excludes:- specialised cleaning activities, such as window cleaning, chimney cleaning, cleaning of fireplaces, stoves, furnaces, incinerators, boilers, ventilation ducts, exhaust units, see 81.22 ', N'81.2 ', 854)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (856, 399336, 4, N'81.22 ', N'Other building and industrial cleaning activities ', N'8129 ', N'This class includes:- exterior cleaning of buildings of all types, including offices, factories, shops, institutions and other business and professional premises and multiunit residential buildings- specialised cleaning activities for buildings such as window cleaning, chimney cleaning and cleaning of fireplaces, stoves, furnaces, incinerators, boilers, ventilation ducts and exhaust units- cleaning of industrial machinery- other building and industrial cleaning activities, n.e.c. ', N'', N'- Cleaning of water supply pipelines ', N'This class excludes:- steam cleaning and blasting and similar activities for building exteriors, see 43.99 ', N'81.2 ', 854)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (857, 399337, 4, N'81.29 ', N'Other cleaning activities ', N'8129 ', N'This class includes:- swimming pool cleaning and maintenance activities- cleaning of trains, buses, planes, etc.- cleaning of the inside of road and sea tankers - disinfecting and exterminating activities- bottle cleaning- street sweeping and snow and ice removal- other cleaning activities, n.e.c. ', N'', N'', N'This class excludes:- agriculture pest control, see 01.61- automobile cleaning, car wash, see 45.20 ', N'81.2 ', 854)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (858, 399338, 3, N'81.3 ', N'Landscape service activities ', N'813 ', N'', N'', N'', N'', N'81 ', 851)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (859, 399339, 4, N'81.30 ', N'Landscape service activities ', N'8130 ', N'This class includes:- planting, care and maintenance of:  • parks and gardens for:    • private and public housing    • public and semi-public buildings (schools, hospitals, administrative buildings, church buildings etc.)    • municipal grounds (parks, green areas, cemeteries etc.)    • highway greenery (roads, train lines and tramlines, waterways, ports)    • industrial and commercial buildings- greenery for:  • buildings (roof gardens, facade greenery, indoor gardens etc.)  • sports grounds (football fields, golf courses etc.), play grounds, lawns for sunbathing and other recreational parks  • stationary and flowing water (basins, alternating wet areas, ponds, swimming pools, ditches, watercourses, plant sewage systems)- plants for protection against noise, wind, erosion, visibility and dazzling ', N'', N'', N'This class excludes:- commercial production and planting for commercial production of plants, trees, see divisions 01, 02- tree nurseries and forest tree nurseries, see 01.30, 02.10- keeping the land in good environmental condition for agricultural use, see 01.61- construction activities for landscaping purposes, see section F- landscape design and architecture activities, see 71.11 ', N'81.3 ', 858)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (860, 399340, 2, N'82 ', N'Office administrative, office support and other business support activities ', N'82 ', N'This division includes the provision of a range of day-to-day office administrative services, as well as ongoing routine business support functions for others, on a contract or fee basis. ', N'This division also includes all support service activities typically provided to businesses not elsewhere classified. ', N'', N'Units classified in this division do not provide operating staff to carry out the complete operations of a business. ', N'N ', 813)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (861, 399341, 3, N'82.1 ', N'Office administrative and support activities ', N'821 ', N'This group includes the provision of a range of day-to-day office administrative services, such as financial planning, billing and record keeping, personnel and physical distribution and logistics for others on a contract or fee basis. ', N'This group also includes support activities for others on a contract or fee basis, that are ongoing routine business support functions that businesses and organisations traditionally do for themselves. ', N'', N'Units classified in this group do not provide operating staff to carry out the complete operations of a business. Units engaged in one particular aspect of these activities are classified according to that particular activity. ', N'82 ', 860)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (862, 399342, 4, N'82.11 ', N'Combined office administrative service activities ', N'8211 ', N'This class includes the provision of a combination of day-to-day office administrative services, such as reception, financial planning, billing and record keeping, personnel and mail services etc. for others on a contract or fee basis. ', N'', N'', N'This class excludes:- provision of only one particular aspect of these activities, see class according to that particular activity- provision of the operating staff without supervision, see 78 ', N'82.1 ', 861)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (863, 399343, 4, N'82.19 ', N'Photocopying, document preparation and other specialised office support activities ', N'8219 ', N'This class includes a variety of copying, document preparation and specialised office support activities. The document copying/printing activities included here cover only short-run type printing activities.This class includes: - document preparation- document editing or proofreading - typing and word processing - secretarial support services- transcription of documents, and other secretarial services- letter or resume writing - provision of mailbox rental and other postal and mailing services, such as pre-sorting, addressing, etc. - photocopying - duplicating- blueprinting- other document copying services (without also providing printing services, such as offset printing, quick printing, digital printing, pre-press services) ', N'', N'- Document and image scanning activities- Formatting of eBooks- Document preparation and pre-archiving ', N'This class excludes:- printing of documents (offset printing, quick printing etc.), see 18.12- pre-press services, see 18.13- developing and organising mail advertising campaigns, see 73.11- specialised stenotype services such as court reporting, see 82.99- public stenography services, see 82.99 ', N'82.1 ', 861)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (864, 399344, 3, N'82.2 ', N'Activities of call centres ', N'822 ', N'', N'', N'', N'', N'82 ', 860)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (865, 399345, 4, N'82.20 ', N'Activities of call centres ', N'8220 ', N'This class includes the activities of: - inbound call centres, answering calls from clients by using human operators, automatic call distribution, computer telephone integration, interactive voice response systems or similar methods to receive orders, provide product information, deal with customer requests for assistance or address customer complaints - outbound call centres using similar methods to sell or market goods or services to potential customers, undertake market research or public opinion polling and similar activities for clients ', N'', N'', N'', N'82.2 ', 864)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (866, 399346, 3, N'82.3 ', N'Organisation of conventions and trade shows ', N'823 ', N'', N'', N'', N'', N'82 ', 860)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (867, 399347, 4, N'82.30 ', N'Organisation of conventions and trade shows ', N'8230 ', N'This class includes the organisation, promotion and/or management of events, such as business and trade shows, conventions, conferences and meetings, whether or not including the management and provision of the staff to operate the facilities in which these events take place. ', N'', N'', N'', N'82.3 ', 866)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (868, 399348, 3, N'82.9 ', N'Business support service activities n.e.c. ', N'829 ', N'This group includes the activities of collection agencies, credit bureaus and all support activities typically provided to businesses not elsewhere classified. ', N'', N'', N'', N'82 ', 860)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (869, 399349, 4, N'82.91 ', N'Activities of collection agencies and credit bureaus ', N'8291 ', N'This class includes:- collection of payments for claims and remittance of payments collected to the clients, such as bill or debt collection services- compiling of information, such as credit and employment histories on individuals and credit histories on businesses and providing the information to financial institutions, retailers and others who have a need to evaluate the creditworthiness of these persons and businesses ', N'', N'- Credit score information compilation activities ', N'', N'82.9 ', 868)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (870, 399350, 4, N'82.92 ', N'Packaging activities ', N'8292 ', N'This class includes:- packaging activities on a fee or contract basis, whether or not these involve an automated process:  • bottling of liquids, including beverages and food  • packaging of solids (blister packaging, foil-covered etc.)  • security packaging of pharmaceutical preparations  • labelling, stamping and imprinting  • parcel-packing and gift-wrapping ', N'', N'- Potato powder/flakes packaging under inert atmosphere- Sterilisation by ionisation ', N'This class excludes:- manufacture of soft drinks and production of mineral water, see 11.07- packaging activities incidental to transport, see 52.29 ', N'82.9 ', 868)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (871, 399351, 4, N'82.99 ', N'Other business support service activities n.e.c. ', N'8299 ', N'This class includes:- providing verbatim reporting and stenotype recording of live legal proceedings and transcribing subsequent recorded materials, such as:  • court reporting or stenotype recording services  • public stenography services- real-time (i.e. simultaneous) closed captioning of live television performances of meetings, conferences - address bar coding services- bar code imprinting services- fundraising organisation services on a contract or fee basis- repossession services- parking meter coin collection services- activities of independent auctioneers - administration of loyalty programmes- other support activities typically provided to businesses not elsewhere classified ', N'', N'- Restaurant voucher handling service activities- Providing assistance in motor vehicle registration- Relocation service activities- Leisure activities gift package administration- Voucher issuing and handling activities- Charity crowd funding- Credit intermediation ', N'This class excludes:- provision of document transcription services, see 82.19- providing film or tape captioning or subtitling services, see 59.12 ', N'82.9 ', 868)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (872, 399352, 1, N'O ', N'PUBLIC ADMINISTRATION AND DEFENCE; COMPULSORY SOCIAL SECURITY ', N'O ', N'This section includes activities of a governmental nature, normally carried out by the public administration. This includes the enactment and judicial interpretation of laws and their pursuant regulation, as well as the administration of programmes based on them, legislative activities, taxation, national defence, public order and safety, immigration services, foreign affairs and the administration of government programmes. The legal or institutional status is not, in itself, the determining factor for an activity to belong in this section, rather than the activity being of a nature specified in the previous paragraph. This means that activities classified elsewhere in NACE do not fall under this section, even if carried out by public entities. For example, administration of the school system (i.e. regulations, checks, curricula) falls under this section, but teaching itself does not (see section P), and a prison or military hospital is classified to health (see section Q). Similarly, some activities described in this section may be carried out by non-government units. ', N'This section also includes compulsory social security activities. ', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (873, 399353, 2, N'84 ', N'Public administration and defence; compulsory social security ', N'84 ', N'', N'', N'', N'', N'O ', 872)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (874, 399354, 3, N'84.1 ', N'Administration of the State and the economic and social policy of the community ', N'841 ', N'This group includes general administration (e.g. executive, legislative, financial administration etc. at all levels of government) and supervision in the field of social and economic life. ', N'', N'', N'', N'84 ', 873)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (875, 399355, 4, N'84.11 ', N'General public administration activities ', N'8411 ', N'This class includes:- executive and legislative administration of central, regional and local bodies- administration and supervision of fiscal affairs:  • operation of taxation schemes  • duty/tax collection on goods and tax violation investigation  • customs administration- budget implementation and management of public funds and public debt:  • raising and receiving of money and control of their disbursement- administration of overall (civil) research and development policy and associated funds- administration and operation of overall economic and social planning and statistical services at the various levels of government ', N'', N'', N'This class excludes:- operation of government owned or occupied buildings, see 68.2, 68.3- administration of research and development policies intended to increase personal well-being and of associated funds, see 84.12- administration of research and development policies intended to improve economic performance and competitiveness, see 84.13- administration of defence-related research and development policies and of associated funds, see 84.22- operation of government archives, see 91.01 ', N'84.1 ', 874)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (876, 399356, 4, N'84.12 ', N'Regulation of the activities of providing health care, education, cultural services and other social services, excluding social security ', N'8412 ', N'This class includes:- public administration of programmes aimed to increase personal well-being:  • health  • education  • culture  • sport  • recreation  • environment  • housing  • social services- public administration of research and development policies and associated funds for these areas ', N'This class also includes: - sponsoring of recreational and cultural activities- distribution of public grants to artists- administration of potable water supply programmes- administration of waste collection and disposal operations- administration of environmental protection programmes- administration of housing programmes ', N'- Public health institutes ', N'This class excludes: - sewage, refuse disposal and remediation activities, see divisions 37, 38, 39- compulsory social security activities, see 84.30- education activities, see section P- human health-related activities, see division 86- museums and other cultural institutions, see division 91- activities of government operated libraries and archives, see 91.01- sporting or other recreational activities, see division 93 ', N'84.1 ', 874)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (877, 399357, 4, N'84.13 ', N'Regulation of and contribution to more efficient operation of businesses ', N'8413 ', N'This class includes:- public administration and regulation, including subsidy allocation, for different economic sectors:   • agriculture  • land use  • energy and mining resources  • infrastructure  • transport  • communication  • hotels and tourism  • wholesale and retail trade- administration of research and development policies and associated funds to improve economic performance- administration of general labour affairs- implementation of regional development policy measures, e.g. to reduce unemployment ', N'', N'', N'This class excludes:- research and experimental development activities, see division 72 ', N'84.1 ', 874)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (878, 399358, 3, N'84.2 ', N'Provision of services to the community as a whole ', N'842 ', N'This group includes foreign affairs, defence and public order and safety activities. ', N'', N'', N'', N'84 ', 873)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (879, 399359, 4, N'84.21 ', N'Foreign affairs ', N'8421 ', N'This class includes:- administration and operation of the ministry of foreign affairs and diplomatic and consular missions stationed abroad or at offices of international organisations- administration, operation and support for information and cultural services intended for distribution beyond national boundaries- aid to foreign countries, whether or not routed through international organisations- provision of military aid to foreign countries- management of foreign trade, international financial and foreign technical affairs ', N'', N'', N'This class excludes:- international disaster or conflict refugee services, see 88.99 ', N'84.2 ', 878)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (880, 399360, 4, N'84.22 ', N'Defence activities ', N'8422 ', N'This class includes:- administration, supervision and operation of military defence affairs and land, sea, air and space defence forces such as:  • combat forces of army, navy and air force  • engineering, transport, communications, intelligence, material, personnel and other non-combat forces and commands  • reserve and auxiliary forces of the defence establishment  • military logistics (provision of equipment, structures, supplies etc.)  • health activities for military personnel in the field- administration, operation and support of civil defence forces- support for the working out of contingency plans and the carrying out of exercises in which civilian institutions and populations are involved- administration of defence-related research and development policies and related funds ', N'', N'', N'This class excludes:- research and experimental development activities, see division 72- provision of military aid to foreign countries, see 84.21- activities of military tribunals, see 84.23 - provision of supplies for domestic emergency use in case of peacetime disasters, see 84.24 - educational activities of military schools, colleges and academies, see 85.4- activities of military hospitals, see 86.10 ', N'84.2 ', 878)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (881, 399361, 4, N'84.23 ', N'Justice and judicial activities ', N'8423 ', N'This class includes:- administration and operation of administrative civil and criminal law courts, military tribunals and the judicial system, including legal representation and advice on behalf of the government or when provided by the government in cash or services- rendering of judgements and interpretations of the law- arbitration of civil actions- prison administration and provision of correctional services, including rehabilitation services, regardless of whether their administration and operation is done by government units or by private units on a contract or fee basis ', N'', N'', N'This class excludes:- advice and representation in civil, criminal and other cases, see 69.10- activities of prison schools, see division 85- activities of prison hospitals, see 86.10 ', N'84.2 ', 878)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (882, 399362, 4, N'84.24 ', N'Public order and safety activities ', N'8423 ', N'This class includes:- administration and operation of regular and auxiliary police forces supported by public authorities and of port, border, coastguards and other special police forces, including traffic regulation, alien registration, maintenance of arrest records- provision of supplies for domestic emergency use in case of peacetime disasters ', N'', N'', N'This class excludes:- operation of police laboratories, see 71.20- administration and operation of military armed forces, see 84.22 ', N'84.2 ', 878)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (883, 399363, 4, N'84.25 ', N'Fire service activities ', N'8423 ', N'This class includes:- fire fighting and fire prevention:  • administration and operation of regular and auxiliary fire brigades in fire prevention, fire fighting, rescue of persons and animals, assistance in civic disasters, floods, road accidents etc. ', N'', N'', N'This class excludes:- forestry fire protection and fire fighting services, see 02.40- oil and gas field fire fighting, see 09.10- fire fighting and fire prevention services at airports provided by non-specialised units, see 52.23 ', N'84.2 ', 878)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (884, 399364, 3, N'84.3 ', N'Compulsory social security activities ', N'843 ', N'', N'', N'', N'', N'84 ', 873)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (885, 399365, 4, N'84.30 ', N'Compulsory social security activities ', N'8430 ', N'This class includes:- funding and administration of government-provided social security programmes:  • sickness, work-accident and unemployment insurance  • retirement pensions  • programmes covering losses of income due to maternity, temporary disablement, widowhood etc. ', N'', N'', N'This class excludes:- non-compulsory social security, see 65.30- provision of welfare services and social work (without accommodation), see 88.10, 88.99 ', N'84.3 ', 884)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (886, 399366, 1, N'P ', N'EDUCATION ', N'P ', N'This section includes education at any level or for any profession. The instructions may be oral or written and may be provided by radio, television, Internet or via correspondence. It includes education by the different institutions in the regular school system at its different levels as well as adult education, literacy programmes etc. Also included are military schools and academies, prison schools etc. at their respective levels. The section includes public as well as private education.For each level of initial education, the classes include special education for physically or mentally handicapped pupils. The breakdown of the categories in this section is based on the level of education offered as defined by the levels of ISCED 1997. The activities of educational institutions providing courses on ISCED level 0 are classified in 85.10, on ISCED level 1 in 85.20, on ISCED levels 2-3 in group 85.3, on ISCED level 4 in 85.41 and on ISCED level 5-6 in 85.42. ', N'This section also includes instruction primarily concerned with sport and recreational activities such as tennis or golf and education support activities. ', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (887, 399367, 2, N'85 ', N'Education ', N'85 ', N'', N'', N'', N'', N'P ', 886)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (888, 399368, 3, N'85.1 ', N'Pre-primary education ', N'851 ', N'', N'', N'', N'', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (889, 399369, 4, N'85.10 ', N'Pre-primary education ', N'8510 ', N'This class includes:- pre-primary education (education preceding the first level). Pre-primary education is defined as the initial stage of organised instruction designed primarily to introduce very young children to a school-type environment, that is, to provide a bridge between the home and a school-based atmosphere. ', N'', N'', N'This class excludes:- child day-care activities, see 88.91 ', N'85.1 ', 888)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (890, 399370, 3, N'85.2 ', N'Primary education ', N'851 ', N'', N'', N'', N'', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (891, 399371, 4, N'85.20 ', N'Primary education ', N'8510 ', N'This class includes primary education: the furnishing of academic courses and associated course work that give students a sound basic education in reading, writing and mathematics and an elementary understanding of other subjects such as history, geography, natural science, social science, art and music. Such education is generally provided for children, however the provision of literacy programmes within or outside the school system, which are similar in content to programmes in primary education but are intended for those considered too old to enter elementary schools, is also included (i.e. adult literacy programmes). ', N'', N'', N'This class excludes:- adult education as defined in 85.5- child day-care activities, including day nurseries for pupils, see 88.91 ', N'85.2 ', 890)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (892, 399372, 3, N'85.3 ', N'Secondary education ', N'852 ', N'This group includes the provision of general secondary and technical and vocational secondary education. ', N'', N'', N'This group excludes:- adult education as defined in 85.5 ', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (893, 399373, 4, N'85.31 ', N'General secondary education ', N'8521 ', N'This class includes provision of the type of education that lays the foundation for lifelong learning and human development and is capable of furthering education opportunities. Such units provide programmes that are usually on a more subject-oriented pattern using more specialised teachers, and more often employ several teachers conducting classes in their field of specialisation. Subject specialisation at this level often begins to have some influence even on the educational experience of those pursuing a general programme. Such programmes are designated to qualify students either for technical and vocational education or for entrance to higher education without any special subject prerequisite.This class includes:- lower general secondary education corresponding more or less to the period of compulsory school attendance- upper general secondary education giving, in principle, access to higher education ', N'', N'', N'', N'85.3 ', 892)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (894, 399374, 4, N'85.32 ', N'Technical and vocational secondary education ', N'8522 ', N'This class includes provision of education typically emphasising subject-matter specialisation and instruction in both theoretical background and practical skills generally associated with present or prospective employment. The aim of a programme can vary from preparation for a general field of employment to a very specific job.This class includes:- technical and vocational education below the level of higher education as defined in 85.4 ', N'This class also includes:- tourist guide instruction- instruction for chefs, hoteliers and restaurateurs- cosmetology and barber schools- computer repair training- driving schools for occupational drivers e.g. of trucks, buses, coaches, schools for professional pilots ', N'', N'This class excludes:- technical and vocational higher education, see 85.4- performing art instruction for recreation, hobby and self-development purposes, see 85.52- automobile driving schools not intended for occupational drivers, see 85.53- job training forming part of social work activities without accommodation, see 88.10, 88.99 ', N'85.3 ', 892)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (895, 399375, 3, N'85.4 ', N'Higher education ', N'853 ', N'This group includes the furnishing of post-secondary non-tertiary and academic courses and granting of degrees at baccalaureate, graduate or post-graduate level. The requirement for admission is a diploma at least at upper secondary education level. ', N'', N'', N'This group excludes:- adult education as defined in 85.5 ', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (896, 399376, 4, N'85.41 ', N'Post-secondary non-tertiary education ', N'8530 ', N'This class includes provision of post-secondary education, which cannot be considered tertiary education. For example provision of supplementary post-secondary education to prepare for tertiary education or post-secondary non-tertiary vocational. ', N'', N'', N'', N'85.4 ', 895)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (897, 399377, 4, N'85.42 ', N'Tertiary education ', N'8530 ', N'This class includes:- first, second and third stages of tertiary education ', N'This class also includes:- performing arts schools providing tertiary education ', N'', N'', N'85.4 ', 895)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (898, 399378, 3, N'85.5 ', N'Other education ', N'854 ', N'This group includes general continuing education and continuing vocational education and training for any profession, hobby or self-development purposes.It includes camps and schools offering instruction in athletic activities to groups or individuals, foreign language instruction, instruction in the arts, drama or music or other instruction or specialised training, not comparable to the education in groups 85.1 - 85.4. ', N'', N'', N'It excludes educational activities as outlined in groups 85.1-85.4, i.e. pre-primary education, primary education, secondary education or higher education. ', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (899, 399379, 4, N'85.51 ', N'Sports and recreation education ', N'8541 ', N'This class includes the provision of instruction in athletic activities to groups of individuals, such as by camps and schools. Overnight and day sports instruction camps are also included. It does not include academic schools, colleges and universities. Instruction may be provided in diverse settings, such as the unit''s or client''s training facilities, educational institutions or by other means. Instruction provided in this class is formally organised.This class includes:- sports instruction (baseball, basketball, cricket, football, etc)- camps, sports instruction- gymnastics instruction- riding instruction, academies or schools- swimming instruction- professional sports instructors, teachers, coaches- martial arts instruction- card game instruction (such as bridge)- yoga instruction ', N'', N'', N'This class excludes:- cultural education, see 85.52 ', N'85.5 ', 898)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (900, 399380, 4, N'85.52 ', N'Cultural education ', N'8542 ', N'This class includes provision of instruction in the arts, drama and music. Units giving this type of instructions might be named "schools”, "studios”, "classes” etc. They provide formally organised instruction, mainly for hobby, recreational or self-development purposes, but such instruction does not lead to a professional diploma, baccalaureate or graduate degree.This class includes:- piano teachers and other music instruction- art instruction- dance instruction and dance studios- drama schools (except academic)- fine arts schools (except academic)- performing arts schools (except academic)- photography schools (except commercial) ', N'', N'', N'This class excludes:- foreign language instruction, see 85.59 ', N'85.5 ', 898)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (901, 399381, 4, N'85.53 ', N'Driving school activities ', N'8549 ', N'', N'This class also includes:- flying, sailing, shipping schools not issuing commercial certificates and permits ', N'', N'This class excludes:- driving schools for occupational drivers, see 85.32 ', N'85.5 ', 898)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (902, 399382, 4, N'85.59 ', N'Other education n.e.c. ', N'8549 ', N'This class includes:- education that is not definable by level- academic tutoring - learning centres offering remedial courses- professional examination review courses- language instruction and conversational skills instruction- computer training- religious instruction ', N'This class also includes:- lifeguard training- survival training- public speaking training- speed reading instruction ', N'- Employee training ', N'This class excludes:- adult literacy programmes see 85.20- general secondary education, see 85.31- technical and vocational secondary education, see 85.32- higher education, see 85.4 ', N'85.5 ', 898)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (903, 399383, 3, N'85.6 ', N'Educational support activities ', N'855 ', N'', N'', N'', N'', N'85 ', 887)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (904, 399384, 4, N'85.60 ', N'Educational support activities ', N'8550 ', N'This class includes:- provision of non-instructional activities that support educational processes or systems:  • educational consulting  • educational guidance counselling activities  • educational testing evaluation activities  • educational testing activities  • organisation of student exchange programmes ', N'', N'- Examination and testing of pilots ', N'This class excludes:- research and experimental development on social sciences and humanities, see 72.20 ', N'85.6 ', 903)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (905, 399385, 1, N'Q ', N'HUMAN HEALTH AND SOCIAL WORK ACTIVITIES ', N'Q ', N'This section includes the provision of health and social work activities. Activities include a wide range of activities, starting from health care provided by trained medical professionals in hospitals and other facilities, over residential care activities that still involve a degree of health care activities to social work activities without any involvement of health care professionals. ', N'', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (906, 399386, 2, N'86 ', N'Human health activities ', N'86 ', N'This division includes activities of short- or long-term hospitals, general or specialty medical, surgical, psychiatric and substance abuse hospitals, sanatoria, preventoria, medical nursing homes, asylums, mental hospital institutions, rehabilitation centres, leprosaria and other human health institutions which have accommodation facilities and which engage in providing diagnostic and medical treatment to inpatients with any of a wide variety of medical conditions. ', N'It also includes medical consultation and treatment in the field of general and specialised medicine by general practitioners and medical specialists and surgeons. It includes dental practice activities of a general or specialised nature and orthodontic activities. Additionally, this division includes activities for human health not performed by hospitals or by practicing medical doctors but by paramedical practitioners legally recognised to treat patients. ', N'', N'', N'Q ', 905)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (907, 399387, 3, N'86.1 ', N'Hospital activities ', N'861 ', N'', N'', N'', N'', N'86 ', 906)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (908, 399388, 4, N'86.10 ', N'Hospital activities ', N'8610 ', N'This class includes:- short- or long-term hospital activities, i.e. medical, diagnostic and treatment activities, of general hospitals (e.g. community and regional hospitals, hospitals of non-profit organisations, university hospitals, military-base and prison hospitals) and specialised hospitals (e.g. mental health and substance abuse hospitals, hospitals for infectious diseases, maternity hospitals, specialised sanatoriums).The activities are chiefly directed to inpatients, are carried out under the direct supervision of medical doctors and include:  • services of medical and paramedical staff  • services of laboratory and technical facilities, including radiologic and anaesthesiologic services  • emergency room services  • provision of operating room services, pharmacy services, food and other hospital services  • services of family planning centres providing medical treatment such as sterilisation and termination of pregnancy, with accommodation ', N'', N'', N'This class excludes:- laboratory testing and inspection of all types of materials and products, except medical, see 71.20- veterinary activities, see 75.00- health activities for military personnel in the field, see 84.22- dental practice activities of a general or specialised nature, e.g. dentistry, endodontic and pediatric dentistry; oral pathology, orthodontic activities, see 86.23- private consultants'' services to inpatients, see 86.2- medical laboratory testing, see 86.90- ambulance transport activities, see 86.90 ', N'86.1 ', 907)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (909, 399389, 3, N'86.2 ', N'Medical and dental practice activities ', N'862 ', N'This group includes medical consultation and treatment done by general medical practitioners and medical specialists, including surgeons, dentists etc.These activities can be carried out in private practice, group practices and in hospital outpatient clinics, and in clinics such as those attached to firms, schools, homes for the aged, labour organisations and fraternal organisations, as well as in patients'' homes. ', N'This group also includes:- private consultants'' services to inpatients ', N'', N'', N'86 ', 906)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (910, 399390, 4, N'86.21 ', N'General medical practice activities ', N'8620 ', N'This class includes:- medical consultation and treatment in the field of general medicine carried out by general practitioners. ', N'', N'', N'This class excludes:- inpatient hospital activities, see 86.10- paramedical activities such as those of midwives, nurses and physiotherapists, see 86.90 ', N'86.2 ', 909)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (911, 399391, 4, N'86.22 ', N'Specialist medical practice activities ', N'8620 ', N'This class includes:- medical consultation and treatment in the field of specialised medicine by medical specialists and surgeons ', N'This class also includes:- family planning centres providing medical treatment such as sterilisation and termination of pregnancy, without accommodation ', N'- Hair transplantation ', N'This class excludes:- inpatient hospital activities, see 86.10- activities of midwives, physiotherapists and other paramedical practitioners, see 86.90 ', N'86.2 ', 909)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (912, 399392, 4, N'86.23 ', N'Dental practice activities ', N'8620 ', N'This class includes:- dental practice activities of a general or specialised nature, e.g. dentistry, endodontic and pediatric dentistry; oral pathology - orthodontic activities ', N'This class also includes:- dental activities in operating rooms ', N'', N'This class excludes:- production of artificial teeth, denture and prosthetic appliances by dental laboratories, see 32.50- inpatient hospital activities, see 86.10- activities of dental paramedical personnel such as dental hygienists, see 86.90 ', N'86.2 ', 909)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (913, 399393, 3, N'86.9 ', N'Other human health activities ', N'869 ', N'', N'', N'', N'', N'86 ', 906)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (914, 399394, 4, N'86.90 ', N'Other human health activities ', N'8690 ', N'This class includes:- activities for human health not performed by hospitals or by medical doctors or dentists:  • activities of nurses, midwives, physiotherapists or other paramedical practitioners in the field of optometry, hydrotherapy, medical massage, occupational therapy, speech therapy, chiropody, homeopathy, chiropractic, acupuncture etc.These activities may be carried out in health clinics such as those attached to firms, schools, homes for the aged, labour organisations and fraternal organisations and in residential health facilities other than hospitals, as well as in own consulting rooms, patients'' homes or elsewhere. ', N'This class also includes:- activities of dental paramedical personnel such as dental therapists, school dental nurses and dental hygienists, who may work remote from, but are periodically supervised by, the dentist- activities of medical laboratories such as:  • X-ray laboratories and other diagnostic imaging centres  • blood analysis laboratories- activities of blood banks, sperm banks, transplant organ banks etc.- ambulance transport of patients by any mode of transport including airplanes. These services are often provided during a medical emergency. ', N'- Reflex therapy- Imaging scanning for medical purposes- Colon hydrotherapy- Echography- Activities of shiatsu practicians ', N'This class excludes:- production of artificial teeth, denture and prosthetic appliances by dental laboratories, see 32.50- transfer of patients, with neither equipment for lifesaving nor medical personnel, see divisions 49, 50, 51- non-medical laboratory testing, see 71.20- testing activities in the field of food hygiene, see 71.20- hospital activities, see 86.10- medical and dental practice activities, see 86.2- residential nursing care facilities, see 87.10 ', N'86.9 ', 913)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (915, 399395, 2, N'87 ', N'Residential care activities ', N'87 ', N'This division includes the provision of residential care combined with either nursing, supervisory or other types of care as required by the residents. Facilities are a significant part of the production process and the care provided is a mix of health and social services with the health services being largely some level of nursing services. ', N'', N'', N'', N'Q ', 905)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (916, 399396, 3, N'87.1 ', N'Residential nursing care activities ', N'871 ', N'', N'', N'', N'', N'87 ', 915)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (917, 399397, 4, N'87.10 ', N'Residential nursing care activities ', N'8710 ', N'This class includes:- activities of:  • homes for the elderly with nursing care  • convalescent homes  • rest homes with nursing care  • nursing care facilities  • nursing homes ', N'', N'', N'This class excludes:- in-home services provided by health care professionals, see division 86- activities of the homes for the elderly without or with minimal nursing care, see 87.30- social work activities with accommodation, such as orphanages, children''s boarding homes and hostels, temporary homeless shelters, see 87.90 ', N'87.1 ', 916)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (918, 399398, 3, N'87.2 ', N'Residential care activities for mental retardation, mental health and substance abuse ', N'872 ', N'', N'', N'', N'', N'87 ', 915)
