INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (919, 399399, 4, N'87.20 ', N'Residential care activities for mental retardation, mental health and substance abuse ', N'8720 ', N'This class includes the provision of residential care (but not licensed hospital care) to people with mental retardation, mental illness, or substance abuse problems. Facilities provide room, board, protective supervision and counselling and some health care. This class includes:- activities of:  • facilities for alcoholism or drug addiction treatment  • psychiatric convalescent homes  • residential group homes for the emotionally disturbed  • mental retardation facilities  • mental health halfway houses ', N'This class also includes:- provision of residential care and treatment for patients with mental health and substance abuse illnesses ', N'', N'This class excludes:- mental hospitals, see 86.10- social work activities with accommodation, such as temporary homeless shelters, see 87.90 ', N'87.2 ', 918)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (920, 399400, 3, N'87.3 ', N'Residential care activities for the elderly and disabled ', N'873 ', N'', N'', N'', N'', N'87 ', 915)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (921, 399401, 4, N'87.30 ', N'Residential care activities for the elderly and disabled ', N'8730 ', N'This class includes the provision of residential and personal care services for the elderly and disabled who are unable to fully care for themselves and/or who do not desire to live independently. The care typically includes room, board, supervision, and assistance in daily living, such as housekeeping services. In some instances these units provide skilled nursing care for residents in separate on-site facilities.This class includes:- activities of:  • assisted-living facilities  • continuing care retirement communities  • homes for the elderly with minimal nursing care  • rest homes without nursing care ', N'', N'', N'This class excludes:- activities of homes for the elderly with nursing care, see 87.10- social work activities with accommodation where medical treatment or education are not important elements, see 87.90 ', N'87.3 ', 920)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (922, 399402, 3, N'87.9 ', N'Other residential care activities ', N'879 ', N'', N'', N'', N'', N'87 ', 915)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (923, 399403, 4, N'87.90 ', N'Other residential care activities ', N'8790 ', N'This class includes the provision of residential and personal care services for persons, except the elderly and disabled, who are unable to fully care for themselves or who do not desire to live independently.This class includes:- activities provided on a round-the-clock basis directed to provide social assistance to children and special categories of persons with some limits on ability for self-care, but where medical treatment or education are not important elements:  • orphanages  • children''s boarding homes and hostels  • temporary homeless shelters  • institutions that take care of unmarried mothers and their childrenThe activities may be carried out by government offices or private organisations. ', N'This class also includes:- activities of:  • halfway group homes for persons with social or personal problems  • halfway homes for delinquents and offenders  • juvenile correction homes ', N'', N'This class excludes:- funding and administration of compulsory social security programmes, see 84.30- activities of nursing care facilities, see 87.10- residential care activities for the elderly or disabled, see 87.30- adoption activities, see 88.99- short-term shelter activities for disaster victims, see 88.99 ', N'87.9 ', 922)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (924, 399404, 2, N'88 ', N'Social work activities without accommodation ', N'88 ', N'This division includes the provision of a variety of social assistance services directly to clients. The activities in this division do not include accommodation services, except on a temporary basis. ', N'', N'', N'', N'Q ', 905)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (925, 399405, 3, N'88.1 ', N'Social work activities without accommodation for the elderly and disabled ', N'881 ', N'', N'', N'', N'', N'88 ', 924)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (926, 399406, 4, N'88.10 ', N'Social work activities without accommodation for the elderly and disabled ', N'8810 ', N'This class includes:- social, counselling, welfare, referral and similar services which are aimed at the elderly and disabled in their homes or elsewhere and carried out by government offices or by private organisations, national or local self-help organisations and by specialists providing counselling services:  • visiting of the elderly and disabled  • day-care activities for the elderly or for disabled adults  • vocational rehabilitation and habilitation activities for disabled persons provided that the education component is limited ', N'', N'', N'This class excludes:- funding and administration of compulsory social security programmes, see 84.30- activities similar to those described in this class, but including accommodation, see 87.30- day-care activities for disabled children, see 88.91 ', N'88.1 ', 925)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (927, 399407, 3, N'88.9 ', N'Other social work activities without accommodation ', N'889 ', N'', N'', N'', N'', N'88 ', 924)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (928, 399408, 4, N'88.91 ', N'Child day-care activities ', N'8890 ', N'', N'This class also includes:- activities of day nurseries for pupils, including day-care activities for disabled children ', N'', N'', N'88.9 ', 927)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (929, 399409, 4, N'88.99 ', N'Other social work activities without accommodation n.e.c. ', N'8890 ', N'This class includes:- social, counselling, welfare, refugee, referral and similar services which are delivered to individuals and families in their homes or elsewhere and carried out by government offices or by private organisations, disaster relief organisations and national or local self-help organisations and by specialists providing counselling services:  • welfare and guidance activities for children and adolescents  • adoption activities, activities for the prevention of cruelty to children and others  • household budget counselling, marriage and family guidance, credit and debt counselling services  • community and neighbourhood activities  • activities for disaster victims, refugees, immigrants etc., including temporary or extended shelter for them  • vocational rehabilitation and habilitation activities for unemployed persons provided that the education component is limited  • eligibility determination in connection with welfare aid, rent supplements or food stamps  • day facilities for the homeless and other socially weak groups  • charitable activities like fund-raising or other supporting activities aimed at social work ', N'', N'- Adoption organisation activities- Cultural mediation- Refugee resettlement activities ', N'This class excludes:- funding and administration of compulsory social security programmes, see 84.30- activities similar to those described in this class, but including accommodation, see 87.90 ', N'88.9 ', 927)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (930, 399410, 1, N'R ', N'ARTS, ENTERTAINMENT AND RECREATION ', N'R ', N'This section includes a wide range of activities to meet varied cultural, entertainment and recreational interests of the general public, including live performances, operation of museum sites, gambling, sports and recreation activities. ', N'', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (931, 399411, 2, N'90 ', N'Creative, arts and entertainment activities ', N'90 ', N'This division includes the operation of facilities and provision of services to meet the cultural and entertainment interests of their customers. This includes the production and promotion of, and participation in, live performances, events or exhibits intended for public viewing; the provision of artistic, creative or technical skills for the production of artistic products and live performances. ', N'', N'', N'This division excludes:- the operation of museums of all kinds, botanical and zoological gardens; the preservation of historical sites; and nature reserves activities, see division 91 - gambling and betting activities, see division 92 - sports and amusement and recreation activities, see division 93.Some units that provide cultural, entertainment or recreational facilities and services are classified in other divisions, such as:- motion picture and video production and distribution, see 59.11, 59.12, 59.13- motion picture projection, see 59.14- radio and television broadcasting, see 60.1, 60.2 ', N'R ', 930)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (932, 399412, 3, N'90.0 ', N'Creative, arts and entertainment activities ', N'900 ', N'This group includes activities in the creative and performing arts and related activities. ', N'', N'', N'', N'90 ', 931)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (933, 399413, 4, N'90.01 ', N'Performing arts ', N'9000 ', N'This class includes:- production of live theatrical presentations, concerts and opera or dance productions and other stage productions:  • activities of groups, circuses or companies, orchestras or bands  • activities of individual artists such as actors, dancers, musicians, lecturers or speakers ', N'', N'- Activities of independent organists- Activities of individual/independent photo models ', N'This class excludes:- activities of personal theatrical or artistic agents or agencies, see 74.90- casting activities, see 78.10 ', N'90.0 ', 932)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (934, 399414, 4, N'90.02 ', N'Support activities to performing arts ', N'9000 ', N'This class includes:- support activities to performing arts for production of live theatrical presentations, concerts and opera or dance productions and other stage productions:  • activities of directors, producers, stage-set designers and builders, scene shifters, lighting engineers etc. ', N'This class also includes:- activities of producers or entrepreneurs of arts live events, with or without facilities ', N'- Scenography- Activities of film directors- Organisation of cultural events, such as film festivals, musical or dance festivals ', N'This class excludes:- activities of personal theatrical or artistic agents or agencies, see 74.90- casting activities, see 78.10 ', N'90.0 ', 932)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (935, 399415, 4, N'90.03 ', N'Artistic creation ', N'9000 ', N'This class includes:- activities of individual artists such as sculptors, painters, cartoonists, engravers, etchers etc.- activities of individual writers, for all subjects including fictional writing, technical writing etc.- activities of independent journalists- restoring of works of art such as paintings etc. ', N'', N'- Interior polychrome restoration, furnace tile restoration and decorative painting- Texts and compositions writing on behalf of someone else- Activities of scriptwriters ', N'This class excludes:- manufacture of statues, other than artistic originals, see 23.70- restoring of organs and other historical musical instruments, see 33.19- motion picture and video production, see 59.11, 59.12- restoring of furniture (except museum type restoration), see 95.24 ', N'90.0 ', 932)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (936, 399416, 4, N'90.04 ', N'Operation of arts facilities ', N'9000 ', N'This class includes:- operation of concert and theatre halls and other arts facilities ', N'', N'', N'This class excludes:- operation of cinemas, see 59.14- activities of ticket agencies, see 79.90- operation of museums of all kinds, see 91.02 ', N'90.0 ', 932)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (937, 399417, 2, N'91 ', N'Libraries, archives, museums and other cultural activities ', N'91 ', N'This division includes the activities of libraries and archives; the operation of museums of all kinds, botanical and zoological gardens; the operation of historical sites and nature reserves activities. ', N'It also includes the preservation and exhibition of objects, sites and natural wonders of historical, cultural or educational interest (e.g. world heritage sites, etc). ', N'', N'This division excludes:- sports and amusement and recreation activities such as the operation of bathing beaches and recreation parks, see division 93 ', N'R ', 930)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (938, 399418, 3, N'91.0 ', N'Libraries, archives, museums and other cultural activities ', N'910 ', N'', N'', N'', N'', N'91 ', 937)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (939, 399419, 4, N'91.01 ', N'Library and archives activities ', N'9101 ', N'This class includes:- documentation and information activities of libraries of all kinds, reading, listening and viewing rooms, public archives providing service to the general public or to a special clientele, such as students, scientists, staff, members as well as operation of government archives:  • organisation of a collection, whether specialised or not  • cataloguing collections  • lending and storage of books, maps, periodicals, films, records, tapes, works of art etc.  • retrieval activities in order to comply with information requests etc.- stock photo and movie libraries and services ', N'', N'', N'', N'91.0 ', 938)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (940, 399420, 4, N'91.02 ', N'Museums activities ', N'9102 ', N'This class includes:- operation of museums of all kinds:  • art museums, museums of jewellery, furniture, costumes, ceramics, silverware  • natural history, science and technological museums, historical museums, including military museums   • other specialised museums  • open-air museums ', N'', N'', N'This class excludes:- activities of commercial art galleries, see 47.78- restoration of works of art and museum collection objects, see 90.03- activities of libraries and archives, see 91.01 ', N'91.0 ', 938)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (941, 399421, 4, N'91.03 ', N'Operation of historical sites and buildings and similar visitor attractions ', N'9102 ', N'This class includes:- operation and preservation of historical sites and buildings ', N'', N'', N'This class excludes:- renovation and restoration of historical sites and buildings, see section F ', N'91.0 ', 938)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (942, 399422, 4, N'91.04 ', N'Botanical and zoological gardens and nature reserves activities ', N'9103 ', N'This class includes:- operation of botanical and zoological gardens, including children''s zoos- operation of nature reserves, including wildlife preservation, etc. ', N'', N'', N'This class excludes:- landscape and gardening activities, see 81.30- operation of sport fishing and hunting preserves, see 93.19 ', N'91.0 ', 938)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (943, 399423, 2, N'92 ', N'Gambling and betting activities ', N'92 ', N'This division includes the operation of gambling facilities such as casinos, bingo halls and video gaming terminals and the provision of gambling services, such as lotteries and off-track betting. ', N'', N'', N'', N'R ', 930)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (944, 399424, 3, N'92.0 ', N'Gambling and betting activities ', N'920 ', N'', N'', N'', N'', N'92 ', 943)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (945, 399425, 4, N'92.00 ', N'Gambling and betting activities ', N'9200 ', N'This class includes gambling and betting activities such as:- sale of lottery tickets- operation (exploitation) of coin-operated gambling machines- operation of virtual gambling web sites- bookmaking and other betting operations- off-track betting- operation of casinos, including "floating casinos” ', N'', N'- Quiz app subscription with prizes ', N'', N'92.0 ', 944)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (946, 399426, 2, N'93 ', N'Sports activities and amusement and recreation activities ', N'93 ', N'This division includes the provision of recreational, amusement and sports activities (except museums activities, preservation of historical sites, botanical and zoological gardens and nature reserves activities; and gambling and betting activities). ', N'', N'', N'Excluded from this division are dramatic arts, music and other arts and entertainment such as the production of live theatrical presentations, concerts and opera or dance productions and other stage productions, see division 90. ', N'R ', 930)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (947, 399427, 3, N'93.1 ', N'Sports activities ', N'931 ', N'This group includes the operation of sports facilities; activities of sports teams or clubs primarily participating in live sports events before a paying audience; independent athletes engaged in participating in live sporting or racing events before a paying audience; owners of racing participants such as cars, dogs, horses, etc. primarily engaged in entering them in racing events or other spectator sports events; sports trainers providing specialised services to support participants in sports events or competitions; operators of arenas and stadiums; other activities of organising, promoting or managing sports events, n.e.c. ', N'', N'', N'', N'93 ', 946)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (948, 399428, 4, N'93.11 ', N'Operation of sports facilities ', N'9311 ', N'This class includes:- operation of facilities for indoor or outdoor sports events (open, closed or covered, with or without spectator seating):  • football, hockey, cricket, rugby stadiums  • racetracks for car, dog, horse races  • swimming pools and stadiums  • track and field stadiums  • winter sports arenas and stadiums  • ice-hockey arenas  • boxing arenas  • golf courses  • bowling lanes- organisation and operation of outdoor or indoor sports events for professionals or amateurs by organisations with own facilities ', N'This class includes managing and providing the staff to operate these facilities. ', N'- Operation of squash facilities ', N'This class excludes:- operation of ski lifts, see 49.39- rental of recreation and sports equipment, see 77.21- activities of fitness facilities, see 93.13- park and beach activities, see 93.29 ', N'93.1 ', 947)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (949, 399429, 4, N'93.12 ', N'Activities of sports clubs ', N'9312 ', N'This class includes the activities of sports clubs, which, whether professional, semi-professional or amateur clubs, give their members the opportunity to engage in sporting activities.This class includes:- operation of sports clubs:  • football clubs  • bowling clubs  • swimming clubs  • golf clubs  • boxing clubs  • winter sports clubs  • chess clubs  • track and field clubs  • shooting clubs, etc. ', N'', N'- Advertising space sale, by sports clubs- Majorette activities, as part of sports events ', N'This class excludes:- sports instruction by individual teachers, trainers, see 85.51- operation of sports facilities, see 93.11- organisation and operation of outdoor or indoor sports events for professionals or amateurs by sports clubs with their own facilities, see 93.11 ', N'93.1 ', 947)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (950, 399430, 4, N'93.13 ', N'Fitness facilities ', N'9311 ', N'This class includes:- fitness and body-building clubs and facilities ', N'', N'', N'This class excludes:- sports instruction by individual teachers, trainers, see 85.51 ', N'93.1 ', 947)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (951, 399431, 4, N'93.19 ', N'Other sports activities ', N'9319 ', N'This class includes:- activities of producers or promoters of sports events, with or without facilities- activities of individual own-account sportsmen and athletes, referees, judges, timekeepers etc.- activities of sports leagues and regulating bodies- activities related to promotion of sporting events- activities of racing stables, kennels and garages- operation of sport fishing and hunting preserves- activities of mountain guides- support activities for sport or recreational hunting and fishing ', N'', N'- Provision of personnel and equipment for sporting events- Advertising space sale, by racing stables- Operation of vertical, aerodynamic wind tunnels ', N'This class excludes:- rental of sports equipment, see 77.21- activities of sport and game schools, see 85.51- activities of sports instructors, teachers, coaches, see 85.51- organisation and operation of outdoor or indoor sports events for professionals or amateurs by sports clubs with/without own facilities, see 93.11, 93.12- park and beach activities, see 93.29 ', N'93.1 ', 947)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (952, 399432, 3, N'93.2 ', N'Amusement and recreation activities ', N'932 ', N'This group includes a wide range of units that operate facilities or provide services to meet the varied recreational interests of their patrons. It includes the operation of a variety of attractions, such as mechanical rides, water rides, games, shows, theme exhibits and picnic grounds. ', N'', N'', N'This group excludes sports activities and dramatic arts, music and other arts and entertainment. ', N'93 ', 946)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (953, 399433, 4, N'93.21 ', N'Activities of amusement parks and theme parks ', N'9321 ', N'This class includes activities of amusement parks or theme parks. It includes the operation of a variety of attractions, such as mechanical rides, water rides, games, shows, theme exhibits and picnic grounds. ', N'', N'- Activities of aqua/water parks ', N'', N'93.2 ', 952)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (954, 399434, 4, N'93.29 ', N'Other amusement and recreation activities ', N'9329 ', N'This class includes activities related to entertainment and recreation (except amusement parks and theme parks) not elsewhere classified:- operation (exploitation) of coin-operated games- activities of recreation parks (without accommodation)- operation of recreational transport facilities, e.g. marinas- operation of ski hills- rental of leisure and pleasure equipment as an integral part of recreational facilities- fairs and shows of a recreational nature- activities of beaches, including rental of facilities such as bathhouses, lockers, chairs etc.- operation of dance floors ', N'This class also includes activities of producers or entrepreneurs of live events other than arts or sports events, with or without facilities. ', N'- Operation of computer rooms for playing computer games- Operation of self-service rocking chairs/horses/cars/space ships etc. as amusement/recreation devices- Airsoft and paintball ', N'This class excludes:- operation of teleferics, funiculars, ski and cable lifts, see 49.39- fishing cruises, see 50.10, 50.30- provision of space and facilities for short stay by visitors in recreational parks and forests and campgrounds, see 55.30- trailer parks, recreational camps, hunting and fishing camps, campsites and campgrounds, see 55.30- discotheques, see 56.30- theatrical and circus groups, see 90.01 ', N'93.2 ', 952)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (955, 399435, 1, N'S ', N'OTHER SERVICE ACTIVITIES ', N'S ', N'This section (as a residual category) includes the activities of membership organisations, the repair of computers and personal and household goods and a variety of personal service activities not covered elsewhere in the classification. ', N'', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (956, 399436, 2, N'94 ', N'Activities of membership organisations ', N'94 ', N'This division includes activities of organisations representing interests of special groups or promoting ideas to the general public. These organisations usually have a constituency of members, but their activities may involve and benefit non-members as well. The primary breakdown of this division is determined by the purpose that these organisations serve, namely interests of employers, self-employed individuals and the scientific community (group 94.1), interests of employees (group 94.2) or promotion of religious, political, cultural, educational or recreational ideas and activities (group 94.9). ', N'', N'', N'', N'S ', 955)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (957, 399437, 3, N'94.1 ', N'Activities of business, employers and professional membership organisations ', N'941 ', N'This group includes the activities of units that promote the interests of the members of business and employers organisations. ', N'In the case of professional membership organisations, it also includes the activities of promoting the professional interests of members of the profession. ', N'', N'', N'94 ', 956)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (958, 399438, 4, N'94.11 ', N'Activities of business and employers membership organisations ', N'9411 ', N'This class includes:- activities of organisations whose members'' interests centre on the development and prosperity of enterprises in a particular line of business or trade, including farming, or on the economic growth and climate of a particular geographical area or political subdivision without regard for the line of business.- activities of federations of such associations- activities of chambers of commerce, guilds and similar organisations- dissemination of information, representation before government agencies, public relations and labour negotiations of business and employer organisations ', N'', N'', N'This class excludes:- activities of trade unions, see 94.20 ', N'94.1 ', 957)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (959, 399439, 4, N'94.12 ', N'Activities of professional membership organisations ', N'9412 ', N'This class includes:- activities of organisations whose members'' interests centre chiefly on a particular scholarly discipline or professional practice or technical field, such as medical associations, legal associations, accounting associations, engineering associations, architects associations etc.- activities of associations of specialists engaged in scientific, academic or cultural activities, such as associations of writers, painters, performers of various kinds, journalists etc.- dissemination of information, the establishment and supervision of standards of practice, representation before government agencies and public relations of professional organisations ', N'This class also includes:- activities of learned societies ', N'', N'This class excludes:- education provided by these organisations, see division 85 ', N'94.1 ', 957)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (960, 399440, 3, N'94.2 ', N'Activities of trade unions ', N'942 ', N'', N'', N'', N'', N'94 ', 956)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (961, 399441, 4, N'94.20 ', N'Activities of trade unions ', N'9420 ', N'This class includes:- promoting of the interests of organised labour and union employees ', N'This class also includes:- activities of associations whose members are employees interested chiefly in the representation of their views concerning the salary and work situation, and in concerted action through organisation- activities of single plant unions, of unions composed of affiliated branches and of labour organisations composed of affiliated unions on the basis of trade, region, organisational structure or other criteria ', N'', N'This class excludes:- education provided by such organisations, see division 85 ', N'94.2 ', 960)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (962, 399442, 3, N'94.9 ', N'Activities of other membership organisations ', N'949 ', N'This group includes the activities of units (except business and employers organisations, professional organisations, trade unions) that promote the interests of their members. ', N'', N'', N'', N'94 ', 956)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (963, 399443, 4, N'94.91 ', N'Activities of religious organisations ', N'9491 ', N'This class includes:- activities of religious organisations or individuals providing services directly to worshippers in churches, mosques, temples, synagogues or other places- activities of organisations furnishing monastery and convent services- religious retreat activities ', N'This class also includes:- religious funeral service activities ', N'', N'This class excludes:- education provided by such organisations, see division 85- health activities by such organisations, see division 86- social work activities by such organisations, see divisions 87, 88 ', N'94.9 ', 962)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (964, 399444, 4, N'94.92 ', N'Activities of political organisations ', N'9492 ', N'This class includes:- activities of political organisations and auxiliary organisations such as young people''s auxiliaries associated with a political party. These organisations chiefly engage in influencing decision-taking in public governing bodies by placing members of the party or those sympathetic to the party in political office and involve the dissemination of information, public relations, fund-raising etc. ', N'', N'', N'', N'94.9 ', 962)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (965, 399445, 4, N'94.99 ', N'Activities of other membership organisations n.e.c. ', N'9499 ', N'This class includes:- activities of organisations (not directly affiliated to a political party) furthering a public cause or issue by means of public education, political influence, fund-raising etc.:  • citizens initiative or protest movements  • environmental and ecological movements  • organisations supporting community and educational facilities n.e.c.  • organisations for the protection and betterment of special groups, e.g. ethnic and minority groups  • associations for patriotic purposes, including war veterans'' associations- consumer associations- automobile associations- associations for the purpose of social acquaintanceship such as rotary clubs, lodges etc.- associations of youth, young persons'' associations, student associations, clubs and fraternities etc.- associations for the pursuit of a cultural or recreational activity or hobby (other than sports or games), e.g. poetry, literature and book clubs, historical clubs, gardening clubs, film and photo clubs, music and art clubs, craft and collectors'' clubs, social clubs, carnival clubs etc. ', N'This class also includes:- grant giving activities by membership organisations or others ', N'', N'This class excludes:- charitable activities like fund-raising aimed at social work, see 88.99- activities of professional artistic groups or organisations, see 90.0- activities of sports clubs, see 93.12- activities of professional associations, see 94.12 ', N'94.9 ', 962)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (966, 399446, 2, N'95 ', N'Repair of computers and personal and household goods ', N'95 ', N'This division includes the repair and maintenance of computers peripheral equipment such as desktops, laptops, computer terminals, storage devices and printers. ', N'It also includes the repair of communications equipment such as fax machines, two-way radios and consumer electronics such as radios and TVs, home and garden equipment such as lawn-mowers and blowers, footwear and leather goods, furniture and home furnishings, clothing and clothing accessories, sporting goods, musical instruments, hobby articles and other personal and household goods. ', N'', N'Excluded from this division is the repair of medical and diagnostic imaging equipment, measuring and surveying instruments, laboratory instruments, radar and sonar equipment, see 33.13. ', N'S ', 955)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (967, 399447, 3, N'95.1 ', N'Repair of computers and communication equipment ', N'951 ', N'This group includes the repair and maintenance of computers and peripheral equipment and communications equipment. ', N'', N'', N'', N'95 ', 966)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (968, 399448, 4, N'95.11 ', N'Repair of computers and peripheral equipment ', N'9511 ', N'This class includes the repair of electronic equipment, such as computers and computing machinery and peripheral equipment.This class includes the repair and maintenance of:- desktop computers- laptop computers- magnetic disk drives, flash drives and other storage devices- optical disk drives (CD-RW, CD-ROM, DVD-ROM, DVD-RW)- printers- monitors- keyboards- mice, joysticks and trackball accessories- internal and external computer modems- dedicated computer terminals- computer servers- scanners, including bar code scanners- smart card readers- virtual reality helmets- computer projectors ', N'This class also includes the repair and maintenance of:- computer terminals like automatic teller machines (ATM''s); point-of-sale (POS) terminals, not mechanically operated- hand-held computers (PDA''s) ', N'- Renovation of CDs and DVDs ', N'This class excludes:- the repair and maintenance of carrier equipment modems, see 95.12 ', N'95.1 ', 967)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (969, 399449, 4, N'95.12 ', N'Repair of communication equipment ', N'9512 ', N'This class includes repair and maintenance of communications equipment such as:- cordless telephones- cellular phones- carrier equipment modems- fax machines- communications transmission equipment (e.g. routers, bridges, modems)- two-way radios- commercial TV and video cameras ', N'', N'', N'', N'95.1 ', 967)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (970, 399450, 3, N'95.2 ', N'Repair of personal and household goods ', N'952 ', N'This group includes the repair and servicing of personal and household goods. ', N'', N'', N'', N'95 ', 966)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (971, 399451, 4, N'95.21 ', N'Repair of consumer electronics ', N'9521 ', N'This class includes repair and maintenance of consumer electronics:- repair of consumer electronics:  • television, radio receivers  • video cassette recorders (VCR)  • CD players  • household-type video cameras ', N'', N'', N'', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (972, 399452, 4, N'95.22 ', N'Repair of household appliances and home and garden equipment ', N'9522 ', N'This class includes the repair and servicing household appliances and home and garden equipment:- repair and servicing of household appliances  • refrigerators, stoves, washing machines, clothes dryers, room air conditioners, etc.- repair and servicing of home and garden equipment  • lawnmowers, edgers, snow- and leaf- blowers, trimmers, etc. ', N'', N'', N'This class excludes:- repair of hand held power tools, see 33.12- repair of central air conditioning systems, see 43.22 ', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (973, 399453, 4, N'95.23 ', N'Repair of footwear and leather goods ', N'9523 ', N'This class includes repair and maintenance of footwear and leather goods:- repair of boots, shoes, luggage and the like- fitting of heels ', N'', N'', N'', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (974, 399454, 4, N'95.24 ', N'Repair of furniture and home furnishings ', N'9524 ', N'This class includes:- reupholstering, refinishing, repairing and restoring of furniture and home furnishings including office furniture ', N'', N'', N'', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (975, 399455, 4, N'95.25 ', N'Repair of watches, clocks and jewellery ', N'9529 ', N'This class includes:- repair of watches, clocks and their parts such as watch cases and housings of all materials; movements, chronometers, etc.- repair of jewellery ', N'', N'', N'This class excludes:- repair of time clocks, time/date stamps, time locks and similar time recording devices, see 33.13 ', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (976, 399456, 4, N'95.29 ', N'Repair of other personal and household goods ', N'9529 ', N'This class includes repair of personal and household goods:- repair of bicycles- repair and alteration of clothing- repair of sporting goods (except sporting guns) and camping equipment- repair of books- repair of musical instruments (except organs and historical musical instruments)- repair of toys and similar articles- repair of other personal and household goods- piano-tuning ', N'', N'', N'This class excludes:- industrial engraving of metals, see 25.61- repair of sporting and recreational guns, 33.11- repair of hand held power tools, see 33.12- restoring of organs and other historical musical instruments, see 33.19 ', N'95.2 ', 970)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (977, 399457, 2, N'96 ', N'Other personal service activities ', N'96 ', N'This division includes all service activities not mentioned elsewhere in the classification. Notably it includes types of services such as washing and (dry-)cleaning of textiles and fur products, hairdressing and other beauty treatment, funeral and related activities. ', N'', N'', N'', N'S ', 955)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (978, 399458, 3, N'96.0 ', N'Other personal service activities ', N'960 ', N'', N'', N'', N'', N'96 ', 977)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (979, 399459, 4, N'96.01 ', N'Washing and (dry-)cleaning of textile and fur products ', N'9601 ', N'This class includes:- laundering and dry cleaning, pressing etc., of all kinds of clothing (including fur) and textiles, provided by mechanical equipment, by hand or by self-service coin-operated machines, whether for the general public or for industrial or commercial clients- laundry collection and delivery- carpet and rug shampooing and drapery and curtain cleaning, whether on clients'' premises or not- provision of linens, work uniforms and related items by laundries- diaper supply services ', N'', N'', N'This class excludes:- rental of clothing other than work uniforms, even if cleaning of these goods is an integral part of the activity, see 77.29- repair and alteration of clothing, see 95.29 ', N'96.0 ', 978)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (980, 399460, 4, N'96.02 ', N'Hairdressing and other beauty treatment ', N'9602 ', N'This class includes:- hair washing, trimming and cutting, setting, dyeing, tinting, waving, straightening and similar activities for men and women- shaving and beard trimming- facial massage, manicure and pedicure, make-up etc. ', N'', N'- Hair design- Permanent make-up ', N'This class excludes:- manufacture of wigs, see 32.99 ', N'96.0 ', 978)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (981, 399461, 4, N'96.03 ', N'Funeral and related activities ', N'9603 ', N'This class includes:- burial and incineration of human or animal corpses and related activities:  • preparing the dead for burial or cremation and embalming and morticians'' services  • providing burial or cremation services  • rental of equipped space in funeral parlours- rental or sale of graves- maintenance of graves and mausoleums ', N'', N'', N'This class excludes:- cemetery gardening, see 81.30- religious funeral service activities, see 94.91 ', N'96.0 ', 978)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (982, 399462, 4, N'96.04 ', N'Physical well-being activities ', N'9609 ', N'This class includes:- activities of Turkish baths, sauna and steam baths, solariums, reducing and slendering salons, massage salons etc. ', N'', N'', N'This class excludes:- medical massage and therapy, see 86.90- activities of health, fitness and body-building clubs and facilities, see 93.13 ', N'96.0 ', 978)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (983, 399463, 4, N'96.09 ', N'Other personal service activities n.e.c. ', N'9609 ', N'This class includes:- astrological and spiritualists'' activities- social activities such as escort services, dating services, services of marriage bureaux- pet care services such as boarding, grooming, sitting and training pets- genealogical organisations- activities of tattooing and piercing studios- shoe shiners, porters, valet car parkers etc.- concession operation of coin-operated personal service machines (photo booths, weighing machines, machines for checking blood pressure, coin-operated lockers etc.) ', N'', N'- Dating and other speed networking activities- Tattoo artist activities, using biological substances (such as henna) for temporary decoration- Horse whispering activities- Housesitting activities- Healer activities ', N'This class excludes:- veterinary activities, see 75.00- coin-operated gambling machines, see 92.00- coin-operated washing machines, see 96.01 ', N'96.0 ', 978)
GO
