INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (987, 399467, 4, N'97.00 ', N'Activities of households as employers of domestic personnel ', N'9700 ', N'This class includes the activities of households as employers of domestic personnel such as maids, cooks, waiters, valets, butlers, laundresses, gardeners, gatekeepers, stable-lads, chauffeurs, caretakers, governesses, babysitters, tutors, secretaries etc. It allows the domestic personnel employed to state the activity of their employer in censuses or studies, even though the employer is an individual. The product produced by this activity is consumed by the employing household. ', N'', N'', N'This class excludes:- provision of services such as cooking, gardening etc. by independent service providers (companies or individuals), see according to type of service ', N'97.0 ', 986)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (988, 399468, 2, N'98 ', N'Undifferentiated goods- and services-producing activities of private households for own use ', N'98 ', N'This division includes the undifferentiated subsistence goods-producing and services-producing activities of households. Households should be classified here only if it is impossible to identify a primary activity for the subsistence activities of the household. If the household engages in market activities, it should be classified according to the primary market activity carried out. ', N'', N'', N'', N'T ', 984)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (989, 399469, 3, N'98.1 ', N'Undifferentiated goods-producing activities of private households for own use ', N'981 ', N'', N'', N'', N'', N'98 ', 988)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (990, 399470, 4, N'98.10 ', N'Undifferentiated goods-producing activities of private households for own use ', N'9810 ', N'This class includes the undifferentiated subsistence goods-producing activities of households, that is to say, the activities of households that are engaged in a variety of activities that produce goods for their own subsistence. These activities include hunting and gathering, farming, the production of shelter and clothing and other goods produced by the household for its own subsistence. If households are also engaged in the production of marketed goods, they are classified to the appropriate goods-producing industry of NACE. If they are principally engaged in a specific goods-producing subsistence activity, they are classified to the appropriate goods-producing industry of NACE. ', N'', N'', N'', N'98.1 ', 989)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (991, 399471, 3, N'98.2 ', N'Undifferentiated service-producing activities of private households for own use ', N'982 ', N'', N'', N'', N'', N'98 ', 988)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (992, 399472, 4, N'98.20 ', N'Undifferentiated service-producing activities of private households for own use ', N'9820 ', N'This class includes the undifferentiated subsistence services-producing activities of households. These activities include cooking, teaching, caring for household members and other services produced by the household for its own subsistence. If households are also engaged in the production of multiple goods for subsistence purposes, they are classified to the undifferentiated goods-producing subsistence activities of households. ', N'', N'', N'', N'98.2 ', 991)
GO
