INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (996, 399476, 4, N'99.00 ', N'Activities of extraterritorial organisations and bodies ', N'9900 ', N'This class includes:- activities of international organisations such as the United Nations and the specialised agencies of the United Nations system, regional bodies etc., the International Monetary Fund, the World Bank, the World Customs Organisation, the Organisation for Economic Co-operation and Development, the organisation of Petroleum Exporting Countries, the European Communities, the European Free Trade Association etc. ', N'This class also includes:- activities of diplomatic and consular missions when being determined by the country of their location rather than by the country they represent ', N'', N'', N'99.0 ', 995)
